import React, { Component } from "react";
import Form from "../components/Form";
import Popup from "../components/Pi";

class Lunchbuddy extends Component {
// import from '@react';
// import '@react/dist/css/min.css';

// settings = {
//     theme: 'ios',
//     themeVariant: 'light'
// };

// const signUpBtn = [{
//     text: 'Sign Up',
//     handler: 'set'
// }];

// const logInBtn = [{
//     text: 'Log In',
//     handler: 'set'
// }];

// class App extends {Component} {
    showRegister = () => {
        this.refs.register.instance.show();
    }

    showLogin = () => {
        this.refs.login.instance.show();
    }

    hideLogin = () => {
        this.refs.login.instance.hide();
    }

    render() {
        return (
            <div>
                <Form>
                    <FormGroup>
                        <div className="mbsc-btn-group-block">
                            <Button onClick={this.showRegister}>Show register form</Button>
                            <Button onClick={this.showLogin}>Show login form</Button>
                        </div>
                    </FormGroup>
                </Form>
                <Popup
                    ref="register"
                    display="center"
                    buttons={signUpBtn}
                >
                    <Form>
                        <FormGroup inset>
                            <Input type="text" name="name" placeholder="Name" />
                            <Input type="text" name="email" placeholder="Email" />
                            <Input name="password" type="text" placeholder="Password" passwordToggle="true" iconShow="eye" iconHide="eye-blocked" />
                        </FormGroup>
                    </Form>
                </Popup>
                <Popup
                    ref="login"
                    display="center"
                    buttons={logInBtn}
                >
                    <Form>
                        <FormGroup inset>
                            <Input type="text" name="email" placeholder="Email" />
                            <Input name="password" type="text" placeholder="Password" passwordToggle="true" iconShow="eye" iconHide="eye-blocked" />
                        </FormGroup>
                        <div className="mbsc-align-center">or log in with one of your existing accounts</div>
                        <FormGroup inset>
                            <Button onClick={this.hideLogin} className="md-social-btn mbsc-btn-block" style={{background:'#3c5c9a', color:'#fff'}}>
                                <span className="mbsc-ic mbsc-ic-ion-social-facebook mbsc-pull-left"></span>
                                Log in with Facebook
                            </Button>
                            <Button onClick={this.hideLogin} className="md-social-btn mbsc-btn-block" style={{background:'#28aae1', color:'#fff'}}>
                                <span className="mbsc-ic mbsc-ic-fa-twitter mbsc-pull-left"></span>
                                Log in with Twitter
                            </Button>
                            <Button onClick={this.hideLogin} className="md-social-btn mbsc-btn-block" style={{background:'#d34230', color:'#fff'}}>
                                <span className="mbsc-ic mbsc-ic-fa-google mbsc-pull-left"></span>
                                Log in with Google
                            </Button>
                        </FormGroup>
                    </Form>
                </Popup>
            </div>
        );
    }    
}

export default Lunchbuddy;
